fun String.findStartOfSequence(messageLength: Int) {
    var startOfSequence: Int? = null
    foldIndexed("") { index, sequence, char ->
        if (sequence.toCharArray().distinct().size == messageLength) {
            if (startOfSequence == null) startOfSequence = index
            sequence
        } else if (sequence.length == messageLength) {
            sequence.substring(1, messageLength) + "$char"
        } else {
            sequence + "$char"
        }
    }
    windowed()
    startOfSequence?.let { println(it) }
}